import React from "react";

function FlashCard1Virado(){
    return(
        <div>
            <div className="flashcard-virado">
                O CARD TÁ VIRADOOOOOOOOOOOOOOOOOOOOOO
            </div>
        </div>
    );
}

function FlashCard1(){
    return(
        <div>
            <div className="flashcard1">
                <div className="fracao">
                    1/8
                </div>
                O que é JX?
                <img src="./assets/turn.png" onClick={props.mudaCard}/>
            </div>

            <div className="flashcard-virado">

            </div>
        </div>
    );
}

function TelaFlashcards(){
    const [card, setCard] = React.useState('flashcard1');
    function mudaCard(){
        setCard('flashcard1virado');
    }
    return(
        <div className="tela-flashcards">
                <img className="mini-logo" src="./assets/logo-mini.png"/>
                <div className="caixa-central">
                {card === 'telainicio' ? <FlashCard1 mudaCard={mudaCard}></FlashCard1> : <FlashCard1Virado></FlashCard1Virado>}
                    {/* <FlashCard1/> */}
                </div>
            </div>
    );
}

function TelaInicio(props){
    return(
        <div className="tela-inicio">
            <img src="./assets/logo.png"/>
            <button className="botao-prat-react" onClick={props.mudaPag}>Praticar React<img src="./assets/next.png"/></button>
        </div>

    );
}

function Pagina(){
    const [pagAtual, setPagAtual] = React.useState('telainicio');
    function mudaPag(){
        setPagAtual('telaflashcards');
    }
    return(
        <div className="conteudo">
            {pagAtual === 'telainicio' ? <TelaInicio mudaPag={mudaPag}></TelaInicio> : <TelaFlashcards></TelaFlashcards>}
        </div>
    );
}

export default function Corpo2(){
    return(
        <div>
            <Pagina/>
        </div>
    );
}
